mapper.update:
update demo_user
set username = #{username,jdbcType=VARCHAR},
password = #{password,jdbcType=VARCHAR},
org_id = #{orgId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select u.id,u.username,u.org_id as orgId, o.org_name as orgName FROM demo_user as u join demo_organization as o where 1=1 AND u.org_id = o.org_id
AND u.username like "%"#{username}"%"
AND u.org_id = #{orgId}

select count(1)
from demo_user
where username =#{username}

select

from demo_user
where username = #{username}
and password = #{password}
